=begin
This file is generated by mktest command.
generated at %{generate_time}

%{initial_map}

|
| %{commands}
v

%{processed_map}
score: %{score}
=end

class LambdaLifter
  class %{test_name} < Test::Unit::TestCase
    should "Validatorと同じ結果になる" do
      commands = "%{commands}"
      score = %{score}
      map = <<'EOS'
%{initial_map}
EOS
      processed_map = <<'EOS'
%{processed_map}
EOS

      mine = Mine.new(map)
      commands.each_char do |s|
        mine.step!(s)
      end
      ascii_map = mine.validator_map
      # Validatorはクリア時にOだがMineはクリア時はRなのでその補正
      ascii_map = ascii_map.sub("R", "O") if !/O/.match(ascii_map)
      assert_equal processed_map, ascii_map, <<INPUT
#{map}

|
| #{commands}
v

#{processed_map}
score: #{score}
INPUT
      assert_equal score, mine.score
    end
  end
end
