require_relative "validator_test"

class LambdaLifter
  class %{test_name} < ValidatorTest
    should "Validatorと同じ結果になる" do
      commands = "%{commands}"
      score = %{score}
      map = <<'EOS'
%{initial_map}
EOS
      processed_map = <<'EOS'
%{processed_map}
EOS

      mine = Mine.new(map)
      commands.each_char do |s|
        mine.step!(s)
      end
      assert_equal treat_expected_map(processed_map),
        treat_actual_map(mine.validator_map), <<INPUT
#{map}

|
| #{commands}
v

#{processed_map}
score: #{score}
INPUT
      assert_equal score, mine.score
    end
  end
end
