class LambdaLifter
  class TestFlood1 < Test::Unit::TestCase
    should "Validatorと同じ結果になる" do
      commands = "LLLLRRRRRLLLLLDDRRRRRRLLLLRRRDDLLDA"
      score = 216
      map = <<'EOS'
###########
#....R....#
#.*******.#
#.\\\\\\\.#
#.       .#
#..*\\\*..#
#.#*\\\*#.#
#########L#

Water 1
Flooding 8 
Waterproof 5
EOS
      processed_map = <<'EOS'
###########
#      ...#
#       *.#
# *     \.#
#.**     .#
#..*****..#
#.#*R\\*#.#
#########L#
EOS

      mine = Mine.new(map)
      commands.each_char do |s|
        mine.step!(s)
      end
      ascii_map = mine.validator_map
      # Validatorはクリア時にOだがMineはクリア時はRなのでその補正
      ascii_map = ascii_map.sub("R", "O") if !/O/.match(ascii_map)
      assert_equal processed_map, ascii_map, <<INPUT
#{map}

|
| #{commands}
v

#{processed_map}
score: #{score}
INPUT
      assert_equal score, mine.score
    end
  end
end
