require_relative "validator_test"

class LambdaLifter
  class TestFlood1_1 < ValidatorTest
    should "Validatorと同じ結果になる" do
      commands = "RRRRDDLLLLLLRRWLDUDUDUDUDUDUDUDLRUULDLUURRDDDDRA"
      score = 403
      map = <<'EOS'
###########
#....R....#
#.*******.#
#.\\\\\\\.#
#.       .#
#..*\\\*..#
#.#*\\\*#.#
#########L#

Water 1
Flooding 8 
Waterproof 5
EOS
      processed_map = <<'EOS'
###########
#.        #
#.        #
#.    *   #
#.*  ****.#
#..**R\*..#
#.#*\\\*#.#
#########L#
EOS

      mine = Mine.new(map)
      commands.each_char do |s|
        mine.step!(s)
      end
      assert_equal treat_expected_map(processed_map),
        treat_actual_map(mine.validator_map), <<INPUT
#{map}

|
| #{commands}
v

#{processed_map}
score: #{score}
INPUT
      pend
      assert_equal score, mine.score
    end
  end
end
