class LambdaLifter
  class TestFlood2 < Test::Unit::TestCase
    should "Validatorと同じ結果になる" do
      commands = "RRUDRRULURULLLLDDDL"
      score = 281
      map = <<'EOS'
#######
#..***#
#..\\\#
#...**#
#.*.*\#
LR....#
#######

Flooding 5
Waterproof 3
EOS
      processed_map = <<'EOS'
#######
#..   #
#  *  #
# ..  #
#   **#
O ****#
#######
EOS

      mine = Mine.new(map)
      commands.each_char do |s|
        mine.step!(s)
      end
      ascii_map = mine.ascii_map
      # Validatorはクリア時にOだがMineはクリア時はRなのでその補正
      ascii_map = ascii_map.sub("R", "O") if !/O/.match(ascii_map)
      assert_equal processed_map, ascii_map, <<INPUT
#{map}

|
| #{commands}
v

#{processed_map}
score: #{score}
INPUT
      assert_equal score, mine.score
    end
  end
end
